import emailjs from "@emailjs/browser";
import { useRef } from "react";

export default function Booking() {
  function sendEmail(e: React.FormEvent<HTMLFormElement>) {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_nru69yw",
        "template_pzdbzm3",
        e.currentTarget,
        "-rV69wm0qc92J0Y_6"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
  }
  return (
    <section className="flex pt-24 flex-col items-center bg-phone bg-cover bg-fixed h-screen">
      <div>
        <h1 className="font-title text-4xl lg:text-6xl text-shadow-md text-neutral-600">
          BOOKING
        </h1>
      </div>
      <form action="" className="flex flex-col" onSubmit={sendEmail}>
        <input type="text" placeholder="First Name" name="firstname" />
        <input type="text" placeholder="Last Name" name="lastname" />
        <input type="email" placeholder="Email" name="email" />
        <input type="submit" value="Send" />
      </form>
    </section>
  );
}
